mapper.delete:
DELETE FROM cp_compo_appversion_day
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}


  AND componentkey=#{componentkey,jdbcType=VARCHAR}


  AND appversion=#{appversion,jdbcType=VARCHAR}

mapper.select:
SELECT
  COUNT(*)
FROM cp_compo_appversion_day
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}


  AND componentkey=#{componentkey,jdbcType=VARCHAR}


  AND appversion=#{appversion,jdbcType=VARCHAR}

