mapper.delete:
DELETE FROM st_newuser_hour
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}
  AND hour=#{hour,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}

mapper.select:
SELECT
  COUNT(*)
FROM st_newuser_hour
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}
  AND hour=#{hour,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}

