mapper.delete:
DELETE FROM st_sessionlength_section_day
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}

mapper.select:
SELECT
  COUNT(*)
FROM st_sessionlength_section_day
WHERE
  year=#{year,jdbcType=VARCHAR}
  AND month=#{month,jdbcType=VARCHAR}
  AND day=#{day,jdbcType=VARCHAR}

  AND appkey=#{appkey,jdbcType=VARCHAR}

